package com.example.shiftscheduler.models;

import com.example.shiftscheduler.R;

import java.time.LocalDate;
import java.util.NavigableSet;
import java.util.Set;

public class MorningShift extends ShiftModel {
    private final ShiftTime time = ShiftTime.MORNING;
    private final int icon = R.drawable.ic_morning;

    /**
     * Constructor.
     * @param shiftID    - will probably be auto-generated by future logic
     * @param date       - date of shift
     * @param employees  - set of assigned employees
     * @param employeesNeeded - maximum employees allowed
     */
    public MorningShift(int shiftID, LocalDate date, NavigableSet<EmployeeModel> employees, int employeesNeeded) {
        super(shiftID, date, employees, employeesNeeded);
    }

    /**
     * @return itself
     */
    @Override
    public MorningShift toMorning() {
        return this;
    }

    /**
     * Verifies all employees are available for this shift
     * @return verified
     */
    @Override
    protected boolean verifyEmployeeAvailability() {
        // check all employees are available
        for (EmployeeModel employee : getEmployees()) {
            if (!employee.isAvailable(getDate(), time)) {
                return false;
            }
        }
        return true;
    }

    /**
     * Verifies this morning shift's employees' qualifications according to specifications
     * @return verified
     */
    @Override
    protected boolean verifyEmployeeQualifications() {
        // check that at least one employee is qualified to open
        for (EmployeeModel employee : getEmployees()) {
            // if this employee is qualified to open â†’ return true
        }
        return false;
    }
}
